import React from 'react';

class Form extends React.Component {
  render() {
    return(
      <form>
          <label>
          Campo Select
            <select>
              <option>1</option>
              <option>2</option>
              <option>3</option>
            </select>
          </label>

          <label>
            Input 1
            <input></input>
          </label>

          <label>
            Input 2
            <input></input>
          </label>

          <label>
            Text Area
            <textarea></textarea>
          </label>
        </form>
    );
  }

}

export default Form;
